mapper.select:
select * from wm_news


        and title like concat("%", #{dto.keyword}, "%")


        and channel_id = #{dto.channelId}


        and status = #{dto.status}


        and created_time >= #{dto.beginPubDate}


        and created_time <= #{dto.endPubDate}


        and user_id = #{userId}


order by created_time desc
